mapper.select:
select flower_id as flowerId, flower_name as flowerName, flower_price as flowerPrice, flower_count as flowerCount, flower_picture as flowerPicture, flower_describe as flowerDescribe, flower_classify as flowerClassify, flower_state as flowerState from t_flower
or flower_name like concat(concat("%",#{message}),"%") or flower_describe like concat(concat("%",#{message}),"%") or flower_classify like concat(concat("%",#{message}),"%") or flower_use like concat(concat("%",#{message}),"%")
or flower_classify=#{flowerClassify}
or flower_use=#{flowerUse}
limit #{start},#{count}

select count(*) as allCount from t_flower
or flower_name like concat(concat("%",#{message}),"%") or flower_describe like concat(concat("%",#{message}),"%") or flower_classify like concat(concat("%",#{message}),"%") or flower_use like concat(concat("%",#{message}),"%")
or flower_classify=#{flowerClassify}
or flower_use=#{flowerUse}
